    //sincronizacin

    replace(newBlocks = []){
        //comprobar la longitud de bloque
        if(newBlocks.length < this.blocks.length) throw Error('Cadena recibida no es ms larga que la cadena actual.');
        try{

            //Comprueba la integridad de la blockchain
            validate(newBlocks);
        }catch(error){
            throw Error('Se recibi cadena invlida');
        }

        this.blocks = newBlocks;

        return this.blocks;
    }
}

export default Blockchain;

